#include <SDL2/SDL.h>
#include <SDL2/SDL_image.h>
#include <stdio.h>

const int SCREEN_WIDTH = 640;
const int SCREEN_HEIGHT = 480;
const int FPS = 60;

SDL_Texture* backgroundTexture;
SDL_Texture* snakeTexture;
SDL_Texture* foodTexture;

int snakeX, snakeY, foodX, foodY, score, direction;

int main() {
    if (SDL_Init(SDL_INIT_VIDEO) < 0) {
        printf("SDL_Init failed: %s\n", SDL_GetError());
        return 1;
    }

    SDL_Window* window = SDL_CreateWindow("Snake Game", SDL_WINDOWPOS_CENTERED, SDL_WINDOWPOS_CENTERED, SCREEN_WIDTH, SCREEN_HEIGHT, SDL_WINDOW_SHOWN);
    SDL_Renderer* renderer = SDL_CreateRenderer(window, -1, SDL_RENDERER_ACCELERATED);

    backgroundTexture = IMG_LoadTexture(renderer, "background.png");
    snakeTexture = IMG_LoadTexture(renderer, "snake.png");
    foodTexture = IMG_LoadTexture(renderer, "food.png");

    snakeX = SCREEN_WIDTH / 2;
    snakeY = SCREEN_HEIGHT / 2;
    score = 0;
    direction = 0;

    SDL_Event event;
    int frameTime;
    while (true) {
        while (SDL_PollEvent(&event)) {
            if (event.type == SDL_QUIT) {
                break;
            } else if (event.type == SDL_KEYDOWN) {
                switch (event.key.keysym.sym) {
                    case SDLK_UP: direction = 0; break;
                    case SDLK_DOWN: direction = 1; break;
                    case SDLK_LEFT: direction = 2; break;
                    case SDLK_RIGHT: direction = 3; break;
                }
            }
        }

        switch (direction) {
            case 0: snakeY -= 10; break;
            case 1: snakeY += 10; break;
            case 2: snakeX -= 10; break;
            case 3: snakeX += 10; break;
        }

        if (snakeX < 0 || snakeX >= SCREEN_WIDTH || snakeY < 0 || snakeY >= SCREEN_HEIGHT) {
            break;
        }

        if (snakeX == foodX && snakeY == foodY) {
            score++;
            foodX = rand() % SCREEN_WIDTH;
            foodY = rand() % SCREEN_HEIGHT;
        }

        SDL_RenderClear(renderer);
        SDL_RenderCopy(renderer, backgroundTexture, NULL, NULL);
        SDL_RenderCopy(renderer, snakeTexture, NULL, &(SDL_Rect){snakeX, snakeY, 10, 10});
        SDL_RenderCopy(renderer, foodTexture, NULL, &(SDL_Rect){foodX, foodY, 10, 10});
        SDL_RenderPresent(renderer);

        frameTime = SDL_GetTicks();
        if (frameTime < 1000 / FPS) {
            SDL_Delay(1000 / FPS - frameTime);
        }
    }

    SDL_DestroyTexture(backgroundTexture);
    SDL_DestroyTexture(snakeTexture);
    SDL_DestroyTexture(foodTexture);
    SDL_DestroyRenderer(renderer);
    SDL_DestroyWindow(window);
    SDL_Quit();

    return 0;
}
```

